import React from "react";
import { useState, useEffect } from "react";
import Axios from "axios";
import { withRouter, Link } from "react-router-dom";
import { useParams } from "react-router-dom";

function Profile() {
  const { userId } = useParams();

  const [myProfile, setMyProfile] = useState([]);

  const getFullProfile = `http://localhost:3001/personal_profile/${userId}`;

  useEffect(() => {
    Axios.get(getFullProfile, {
      id: userId,
    }).then((response) => {
      setMyProfile(response.data);
      console.log(response.data);
      console.log("hello");
    });
  }, []);

  /*Profile

  {

      return (
        <div>
          Email = {val.email};
          <Link to={`/work_reference/${val.work_ref}`}>
            View Work Reference
          </Link>
          <br></br>
          <Link to={`/landlord_reference/${val.landlord_ref}`}>
            View Landlord Reference
          </Link>
        </div>
      );
    });
  }

   */

  return (
    <div>
      <div>
        {myProfile.map((val) => {
          return (
            <div class="hero-create-ad vh-120 d-flex align-items-center hero-ad-background">
              <div class="container base-sign-container-create-ad col-6">
                <div class="row mt-5"></div>

                <div class="row justify-content-center">
                  <div class="col-md-4 align-items-center"></div>
                  <div class="col-md-6  form-title-ad ">
                    Description: {val.first_name} | Rent: {val.last_name} |
                    email:{val.email}
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default withRouter(Profile);
