import { useEffect, useState, useContext } from "react";
import Axios from "axios";
import { LoginContext } from "../components/Contexts/LoginContext";
import companyLogo from "../assets/images/main_logo.png";
import { Google } from "react-bootstrap-icons";

export default function Login() {
  const [username, setUserName] = useState("");
  const [password, setPassword] = useState("");

  const [loginStatus, setLoginStatus] = useState("");

  const { setIsAuth } = useContext(LoginContext);
  const { setUserId } = useContext(LoginContext);

  Axios.defaults.withCredentials = true;

  const login = () => {
    Axios.post("http://localhost:3001/login", {
      username: username,
      password: password,
    }).then((response) => {
      if (response.data.message) {
        setLoginStatus(response.data.message);
      } else {
        setLoginStatus(response.data[0].username);
        setIsAuth(true);
        setUserId(response.data[0].id);
        console.log(response.data[0].id);
      }
    });
  };

  useEffect(() => {
    Axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn === true) {
        setLoginStatus(response.data[0].username);
      }
    });
  }, []);

  return (
    <div class="hero-registration-customer  vh-100 d-flex align-items-center hero-sign-up-background">
      <div class="container base-sign-container bg-white col-6">
        <div class="row justify-content-center mt-3">
          <div class="col-md-3"></div>
        </div>

        <div class="row justify-content-center mt-3">
          <div class="col-md-3">
            <img class="companyLogo" src={companyLogo}></img>
            <br></br>
          </div>
        </div>

        <div class="row justify-content-center mt-3">
          <div class="col-md-8 text-center ">
            <p class="social-sign link-style">
              <Google />
              <Link
                style={{ textDecoration: "none" }}
                to="https://accounts.google.com/login"
              >
                Sign-up with Google{" "}
              </Link>
            </p>
          </div>
        </div>

        <div class="row">
          <div class="col-md-12 form-title">
            <label>Username</label>
            <br></br>
            <input
              class="form-control"
              type="text"
              placeholder="username"
              onChange={(e) => {
                setUserName(e.target.value);
              }}
            ></input>
            <br></br>
          </div>
        </div>

        <div class="row">
          <div class="col-md-12 form-title">
            <label>Password</label>
            <br></br>
            <input
              class="form-control"
              type="password"
              placeholder="password"
              onChange={(e) => {
                setPassword(e.target.value);
              }}
            ></input>
            <br></br>
          </div>
        </div>
        <h1>{loginStatus}</h1>

        <button class="form-control submit-tag" onClick={login}>
          Login
        </button>
      </div>
    </div>
  );
}
