import React from "react";
import { useState, useEffect } from "react";
import Axios from "axios";
import { withRouter, Link } from "react-router-dom";
import { useParams } from "react-router-dom";
import FaBsPersonCircle from "react-icons/fa/";

function Profile() {
  const { userId } = useParams();

  const [myProfile, setMyProfile] = useState([]);

  const getFullProfile = `http://localhost:3001/personal_profile/${userId}`;

  useEffect(() => {
    Axios.get(getFullProfile, {
      id: userId,
    }).then((response) => {
      setMyProfile(response.data);
      console.log(response.data);
      console.log("hello");
    });
  }, []);

  /*Profile

  {

      return (
        <div>
          Email = {val.email};
          <Link to={`/work_reference/${val.work_ref}`}>
            View Work Reference
          </Link>
          <br></br>
          <Link to={`/landlord_reference/${val.landlord_ref}`}>
            View Landlord Reference
          </Link>
        </div>
      );
    });
  }

   */

  return (
    <div>
      <div>
        {myProfile.map((val) => {
          return (
            <div>
              <section class="main-content">
                <div class="container">
                  <h1 class="text-center mb-5">Profile Card UI</h1>

                  <div class="row">
                    <div class="col-sm8 offset-sm-2 col-md-6 offset-md-3 col-lg-4 offset-lg-4">
                      <div class="profile-card">
                        <div class="banner"></div>

                        <img
                          src={require(`../assets/photo_references/${val.photo_id}`)}
                          alt=""
                          class="img-circle mx-auto mb3"
                        ></img>

                        <h3 class="mb-4">
                          <FaBsPersonCircle /> {val.first_name} {val.last_name}
                          <div class="text-left mb-4">
                            <p class="mb-2">
                              <i i class="bi bi-envelope"></i>
                              {val.email}
                            </p>
                            <p class="mb-2">
                              <i class="fa fa-phone mr-2">{val.contact_no}</i>
                            </p>
                          </div>
                        </h3>
                      </div>
                    </div>
                  </div>
                </div>
              </section>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default withRouter(Profile);
