import React from "react";
import { useEffect, useState } from "react";

import Axios from "axios";
import AdIndv from "../pages/Ad_indv";

function MyAd() {
  const [dbID, setDbID] = useState(null);
  const [myAds, setMyAds] = useState([]);

  const getMyAds = `http://localhost:3001/adverts/${dbID}`;

  useEffect(() => {
    Axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn === true) {
        setDbID(response.data.user[0].id);
      }
    });
  }, []);

  useEffect(() => {
    Axios.get(getMyAds).then((response) => {
      setMyAds(response.data);
    });
  }, [dbID]);

  /*    <div>
              <AdIndv
                description={val.description}
                rent={val.rent}
                ID={val.id_property_ad}
                property_photo={val.property_photo}
              />
            </div>
            */

  return (
    <div class="hero-ad  d-flex align-items-center hero-ad-background">
      <div class="container base-sign-container-ad col-9">
        <div class="row mt-5"></div>

        <div class="row justify-content-center align-items-center">
          <div class="col-md-4 justify-content-center">
            <img
              class="ad_image"
              src={require(`../assets/property_photos/${val.property_photo}`)}
              alt="interior"
              width="300"
              height="200"
            ></img>
          </div>
          <div class="col-md-6 form-title-ad description-container   ">
            <div class="row description-position mt-3 ">
              <div class="col-md-3 rent-ad-title d-flex justify-content-center">
                Description
              </div>
              <div class="row d-flex justify-content-center mt-2">
                {val.description}
              </div>
            </div>

            <div class="row description-position mt-3 ">
              <div class="col-md-3 rent-ad-title d-flex justify-content-center">
                Rent
              </div>
              <div class="row d-flex justify-content-center mt-2">
                {val.rent}
              </div>
            </div>

            <div class="row mt-3 ">
              <AdIndv
                description={val.description}
                rent={val.rent}
                ID={val.id_property_ad}
                property_photo={val.property_photo}
              />
            </div>

            <br></br>
          </div>
        </div>
      </div>
    </div>
  );
}

export default MyAd;
