import React, { useEffect, useState } from "react";
import Axios from "axios";
import { LoginContext } from "../components/Contexts/LoginContext";

function Rent() {
  const [propertyAdList, setPropertyAdList] = useState([]);

  const [dbID, setDbID] = useState(null);

  useEffect(() => {
    Axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn === true) {
        setDbID(response.data.user[0].id);
      }
    });
  }, []);

  useEffect(() => {
    Axios.get("http://localhost:3001/rent").then((response) => {
      setPropertyAdList(response.data);
    });
  }, []);

  const submitApplication = () => {
    Axios.post("http://localhost:3001/rent", {
      id_propertyAd: 1,
      id: 2,
    }).then(() => {
      alert("application successfull");
    });
  };

  return (
    <div>
      This is the motherfucking rent page
      <div>
        {propertyAdList.map((val) => {
          return (
            <div>
              <h1>
                Description: {val.description} | Rent: {val.rent}
              </h1>
              <button onClick={submitApplication}>Submit Application</button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Rent;
