import React, { useState } from "react";
import Axios from "axios";

function Ad_highlight({ id_property_ad }) {
  const [adInd, setAdInd] = useState([]);
  let adLink = id_property_ad.toString();
  let getIndAd = "http://localhost:3001/advert/";
  let getRequest = getIndAd.concat(adLink);

  const showAd = () => {
    Axios.get(getRequest).then((response) => {
      setAdInd(response.data);
      console.log(response.data);
    });
  };

  return (
    <div>
      <h1>This is your main ad</h1>
      <button onClick={showAd}>Show</button>
      {adInd.map((val) => {
        return (
          <div>
            Description: {val.description}
            Rent: {val.rent}
          </div>
        );
      })}
    </div>
  );
}

export default Ad_highlight;
