import React from "react";
import { useEffect, useState } from "react";
import Axios from "axios";
import { BrowsweRouter as Router, Route } from "react-router-dom";
import Rent from "./Rent";

function MyAd() {
  const [dbID, setDbID] = useState(null);
  const [myAds, setMyAds] = useState([]);

  const getMyAds = `http://localhost:3001/adverts/${dbID}`;

  useEffect(() => {
    Axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn === true) {
        setDbID(response.data.user[0].id);
      }
    });
  }, []);

  const findAd = () => {
    Axios.get(getMyAds).then((response) => {
      setMyAds(response.data);
    });
  };

  return (
    <div>
      <button onClick={findAd}>Find My Ads</button>
      {myAds.map((val) => {
        return (
          <div>
            Description: {val.description} | Rent: {val.rent} | AdID:
            {val.id_property_ad}
            <Router>
              <Route path="/rent" component={Rent}></Route>
            </Router>
          </div>
        );
      })}
    </div>
  );
}

export default MyAd;
